* {
  box-sizing: border-box;
}
*::before,
*::after {
  box-sizing: border-box;
}
* {
  margin: 0;
  padding: 0;
}
ul,
ol {
  list-style: none;
}
a {
  color: inherit;
  text-decoration: none;
}
img {
  max-width: 100%;
  max-height: 100%;
}

body {
  background: #eee;
}
.globalHeader {
  margin: 20px;
}
@media (min-width: 500px) {
  .globalHeader {
    margin: 110px 0 80px;
  }
}
.searchForm {
  display: flex;
  justify-content: space-between;
}
@media (min-width: 500px) {
  .searchForm {
    max-width: 400px;
    margin-left: auto;
    margin-right: auto;
  }
}
.searchForm > input {
  width: 100%;
  margin-right: 10px;
  height: 40px;
  padding: 20px;
  border: 1px solid #eee;
  border-radius: 50px;
  border: 0;
  outline: none;
}
.searchForm > button {
  white-space: nowrap;
  padding: 10px;
  border-radius: 10px;
  border: none;
  background: #0f8cff;
  padding: 0 28px;
  font-size: 20px;
  transition: all 0.8s;
}
.searchForm > button:hover {
  transform: scale(1.1);
  background-color: #0f8cff;
}
.globalMain {
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
}
.siteList {
  display: flex;
  flex-wrap: wrap;
  margin: 20px;
  justify-content: space-between;
}
@media (min-width: 500px) {
  .siteList {
    margin-left: auto;
    margin-right: -25px;
    justify-content: flex-start;
  }
}

.siteList > li {
  margin-bottom: 10px;
}
@media (min-width: 500px) {
  .siteList > li {
    margin-right: 25px;
  }
}
.siteList .site {
  width: 160px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  padding: 20px 0;
  border-radius: 4px;
  transition: all 0.8s;
  position: relative;
  cursor: pointer;
}
.siteList .site:hover {
  transform: scale(1.1);
  box-shadow: 0px 1px 8px 6px rgba(149, 204, 228, 1);
  -webkit-box-shadow: 0px 1px 8px 6px rgba(149, 204, 228, 1);
  -moz-box-shadow: 0px 1px 8px 6px rgba(149, 204, 228, 1);
}
.siteList .site > .logo {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 64px;
  margin-top: 8px;
  text-transform: uppercase;
}
.siteList .site .link {
  font-size: 20px;
  margin-top: 4px;
}
.siteList .site > .close {
  position: absolute;
  right: 5px;
  top: 5px;
  display: none;
  cursor: default;
}
.siteList .site:hover > .close {
  display: block;
}
.siteList .addButton {
  width: 160px;
  border-radius: 4px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  padding: 20px 0;
  border-radius: 4px;
  transition: all 0.8s;
}
.siteList .addButton:hover {
  transform: scale(1.1);
  box-shadow: 0px 1px 8px 6px rgba(149, 204, 228, 1);
  -webkit-box-shadow: 0px 1px 8px 6px rgba(149, 204, 228, 1);
  -moz-box-shadow: 0px 1px 8px 6px rgba(149, 204, 228, 1);
}
.siteList .addButton .icon {
  width: 64px;
  height: 64px;
  font-size: 64px;
  margin-top: 8px;
}
.siteList.addButton .icon-wrapper {
  width: 64px;
  height: 64px;
  display: flex;
  justify-content: center;
  align-items: center;
}
.siteList .addButton .text {
  font-size: 20px;
  margin-top: 4px;
}
